<style lang="scss">
.nav{
  justify-content: center;
}

.footer{
    position: fixed;
    bottom: 0;
    /* left: 10px; */
    /* padding: 3rem; */
    z-index: 10;
    height: 50px;
    background-color: #292929;
    width: 100%;
    display: flex;
}

.footer-block{
  width: 33%;
  text-align: center;
  line-height: 50px;
}

.nav li {
  display: inline-block;
  font-size: 20px;
  margin-right: 10px;
  margin-top: 12px;
  font-size: 15px;
  font-weight: bold;
  color: white;
}

.active-menu{
  border-bottom: 2px solid white;
}

.item {
  display: flex;
  align-items: center;
  font-size: 2.1rem;
  line-height: 3rem;
  color: #fff;
  white-space: nowrap;
  mix-blend-mode: screen;
  filter: drop-shadow(3px -3px 0px #0ff) drop-shadow(3px -3px 0px #f00);
  transform: translate(1px, -1px);
  padding: 0;
  margin: 0;

  &.shaking {
    animation: shaking 0.2s steps(6, end) infinite;
  }

  figcaption {
    padding-left: 0.4rem;
    display: inline;
  }

  .svg-tiktok {
    fill: #fff;
    width: 2.5rem;
    height: 2.5rem;
  }
}

.sub {
  font-size: 1rem;
  margin-top: -0.3rem;
  padding-left: 4.6rem;
  color: white;
}

@keyframes shaking {
  0% {
    filter: drop-shadow(3px 3px 0px #0ff) drop-shadow(-3px -3px 0px #f00);
    transform: translate(1px, 1px);
